import React from 'react';
import './App.css';
import Number from'./Number.js';
import Compter from'./Compter.js';

class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      compter: 0
    }
    this.add = this.add.bind(this)
    this.moin = this.moin.bind(this)
    this.add = this.add.bind(this)
    this.moin = this.moin.bind(this)
    this.add = this.add.bind(this)
  }

  add(){
    console.log("plus")
    this.setState(state =>({
      compter: state.compter + 1
    }))
  }

  moin(){
    console.log("moin")
    this.setState(state =>({
      compter: state.compter + 2
    }))
  }
  trois(){
    console.log("moin")
    this.setState(state =>({
      compter: state.compter + 3
    }))
  }
  quatre(){
    console.log("moin")
    this.setState(state =>({
      compter: state.compter + 4
    }))
  }
  cinque(){
    console.log("moin")
    this.setState(state =>({
      compter: state.compter + 4
    }))
  }

  render() {
    return (
      <div className="App">
        <Number nombre={this.state.compter}></Number>
        <Compter value="1" change={this.add}></Compter>
        <Compter value="2" change={this.moin}></Compter>
        <Compter value="3" change={this.trois}></Compter>
        <Compter value="4" change={this.quatre}></Compter>
        <Compter value="5" change={this.cinque}></Compter>
      </div>
    );
  }
}

export default App;
